/** @typedef { import('.').Player } PlayerClass */

/**
 * @implements {PlayerClass}
 * @todo avoid repeated method signatures, when TypeScript some day can infer it from PlayerClass
 */
export class Player {
  /**
   * @constructor
   */
  constructor() {
  }
  /**
   * @param {string[]} playlist 
   */
  playFrom(playlist) {
    return playlist.map((name) => `play ${name}`).join(', ');
  }
}